import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const localizer = momentLocalizer(moment);

function CustomCalendar() {
  const [events, setEvents] = useState([]);

  const handleSelectSlot = ({ start, end }) => {
    const title = prompt('Zadajte názov udalosti:');
    const notes = prompt('Zadajte poznámku k udalosti (voliteľné):');
    if (title) {
      const newEvent = { title, start, end, notes };
      setEvents([...events, newEvent]);
      toast.success(`Udalosť "${title}" bola pridaná!`);
    }
  };

  const handleSelectEvent = (event) => {
    alert(`Udalosť: ${event.title}\nPoznámka: ${event.notes || 'Žiadna poznámka'}`);
  };

  return (
    <div className="container mt-5">
      <h2>
        <i className="bi bi-calendar"></i> Kalendár
      </h2>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        selectable
        onSelectSlot={handleSelectSlot}
        onSelectEvent={handleSelectEvent}
        messages={{
          today: 'Dnes',
          previous: 'Predchádzajúci',
          next: 'Nasledujúci',
        }}
      />
      <ToastContainer />
    </div>
  );
}

export default CustomCalendar;
