import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, Button, Container, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const navigate = useNavigate();

  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5001/api/employees', {
        headers: { 'x-auth-token': token }
      });
      setEmployees(response.data);
    } catch (err) {
      console.error(err);
      alert('Chyba pri načítaní zoznamu');
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const deleteEmployee = async (id) => {
    if (window.confirm('Naozaj chceš zmazať tohto zamestnanca?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5001/api/employees/${id}`, {
          headers: { 'x-auth-token': token }
        });
        fetchEmployees();
      } catch (err) {
        console.error(err);
        alert('Chyba pri mazaní');
      }
    }
  };

  return (
    <Container className="mt-5">
      <Card className="p-4 shadow">
        <h2 className="text-center mb-4">Zamestnanci</h2>
        <Button className="mb-3" onClick={() => navigate('/add-employee')}>+ Pridať zamestnanca</Button>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Meno</th>
              <th>Priezvisko</th>
              <th>Pozícia</th>
              <th>Oddelenie</th>
              <th>Akcie</th>
            </tr>
          </thead>
          <tbody>
            {employees.map(emp => (
              <tr key={emp._id}>
                <td>{emp.firstName}</td>
                <td>{emp.lastName}</td>
                <td>{emp.position}</td>
                <td>{emp.department}</td>
                <td>
                  <Button variant="warning" size="sm" onClick={() => navigate(`/edit-employee/${emp._id}`)}>Upraviť</Button>{' '}
                  <Button variant="danger" size="sm" onClick={() => deleteEmployee(emp._id)}>Zmazať</Button>{' '}
                  <Button variant="info" size="sm" onClick={() => navigate(`/edit-details/${emp._id}`)}>Firemné údaje</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Card>
    </Container>
  );
};

export default EmployeeList;
