import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Container, Card } from 'react-bootstrap';

const EditEmployee = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [employee, setEmployee] = useState({
    firstName: '',
    lastName: '',
    position: '',
    department: ''
  });

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:5001/api/employees/${id}`, {
          headers: { 'x-auth-token': token }
        });
        setEmployee(response.data);
      } catch (err) {
        console.error(err);
        alert('Chyba pri načítaní zamestnanca');
      }
    };

    fetchEmployee();
  }, [id]);

  const handleChange = (e) => {
    setEmployee({ ...employee, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:5001/api/employees/${id}`, employee, {
        headers: { 'x-auth-token': token }
      });
      navigate('/employees');
    } catch (err) {
      console.error(err);
      alert('Chyba pri ukladaní');
    }
  };

  return (
    <Container className="mt-5">
      <Card className="p-4 shadow">
        <h2 className="text-center mb-4">Upraviť zamestnanca</h2>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Meno</Form.Label>
            <Form.Control name="firstName" value={employee.firstName} onChange={handleChange} required />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Priezvisko</Form.Label>
            <Form.Control name="lastName" value={employee.lastName} onChange={handleChange} required />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Pozícia</Form.Label>
            <Form.Control name="position" value={employee.position} onChange={handleChange} />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Oddelenie</Form.Label>
            <Form.Control name="department" value={employee.department} onChange={handleChange} />
          </Form.Group>
          <Button variant="success" type="submit">Uložiť zmeny</Button>
        </Form>
      </Card>
    </Container>
  );
};

export default EditEmployee;
