// EmployeeDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Card, Container, ListGroup } from 'react-bootstrap';
import axios from 'axios';

function EmployeeDetails() {
  const { id } = useParams();
  const [employee, setEmployee] = useState(null);

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const res = await axios.get(`http://localhost:5001/api/employees/${id}`);
        setEmployee(res.data);
      } catch (error) {
        console.error('Chyba pri načítaní detailov zamestnanca:', error);
        alert('Nepodarilo sa načítať údaje');
      }
    };
    fetchEmployee();
  }, [id]);

  if (!employee) {
    return <p className="text-center mt-5">Načítavam údaje zamestnanca...</p>;
  }

  return (
    <Container className="mt-5">
      <Card className="shadow">
        <Card.Header className="text-center">
          <h2>Detail zamestnanca</h2>
        </Card.Header>
        <Card.Body>
          <ListGroup variant="flush">
            <ListGroup.Item><strong>Meno:</strong> {employee.firstName}</ListGroup.Item>
            <ListGroup.Item><strong>Priezvisko:</strong> {employee.lastName}</ListGroup.Item>
            <ListGroup.Item><strong>Pozícia:</strong> {employee.position}</ListGroup.Item>
            <ListGroup.Item><strong>Oddelenie:</strong> {employee.department}</ListGroup.Item>
            <ListGroup.Item><strong>Dátum pridania:</strong> {new Date(employee.startDate).toLocaleString()}</ListGroup.Item>
          </ListGroup>
        </Card.Body>
      </Card>
    </Container>
  );
}

export default EmployeeDetails;
