import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Container, Card, Table } from 'react-bootstrap';
import axios from 'axios';

function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const navigate = useNavigate();

  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/employees');
      setEmployees(response.data);
    } catch (error) {
      console.error('Chyba pri načítaní zamestnancov:', error);
    }
  };

  const deleteEmployee = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/employees/${id}`);
      setEmployees((prev) => prev.filter((emp) => emp._id !== id));
    } catch (error) {
      console.error('Chyba pri mazaní zamestnanca:', error);
      alert('Chyba pri mazaní');
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  return (
    <Container className="mt-5">
      <Card className="p-4 shadow">
        <h2 className="text-center mb-4">Zamestnanci</h2>
        <Button className="mb-3" onClick={() => navigate('/add-employee')}>
          + Pridať zamestnanca
        </Button>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Meno</th>
              <th>Pozícia</th>
              <th>Oddelenie</th>
              <th>Akcie</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((emp) => (
              <tr key={emp._id}>
                <td>{emp.name || `${emp.firstName} ${emp.lastName}`}</td>
                <td>{emp.position}</td>
                <td>{emp.department}</td>
                <td>
                  <Button
                    variant="warning"
                    size="sm"
                    onClick={() => navigate(`/edit-employee/${emp._id}`)}
                    className="me-2"
                  >
                    Upraviť
                  </Button>
                  <Button
                    variant="danger"
                    size="sm"
                    onClick={() => deleteEmployee(emp._id)}
                  >
                    Zmazať
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Card>
    </Container>
  );
}

export default EmployeeList;
