import React, { useState } from 'react';

function AddEmployee() {
  const [personalData, setPersonalData] = useState({});
  const [contactData, setContactData] = useState({});
  const [addresses, setAddresses] = useState({});
  const [marketing, setMarketing] = useState({});
  const [message, setMessage] = useState('');

  const handlePersonalChange = (e) => {
    setPersonalData({ ...personalData, [e.target.name]: e.target.value });
  };
  const handleContactChange = (e) => {
    setContactData({ ...contactData, [e.target.name]: e.target.value });
  };
  const handleAddressChange = (e) => {
    setAddresses({ ...addresses, [e.target.name]: e.target.value });
  };
  const handleMarketingChange = (e) => {
    setMarketing({ ...marketing, [e.target.name]: e.target.checked });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = {
      ...personalData,
      ...contactData,
      ...addresses,
      ...marketing
    };

    try {
      const response = await fetch('http://localhost:5000/api/employees/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        const err = await response.json();
        setMessage('Chyba pri ukladaní: ' + (err.error || response.statusText));
      } else {
        setMessage('Zamestnanec bol úspešne uložený!');
        // Sem môžeš pridať logiku na vyčistenie formulára alebo presmerovanie
      }
    } catch (error) {
      setMessage('Chyba spojenia s backendom: ' + error.message);
    }
  };

  return (
    <div className="container mt-5" style={{ maxWidth: 850 }}>
      <h2>Pridať zamestnanca</h2>
      {message && <div className="alert alert-info">{message}</div>}
      <form onSubmit={handleSubmit}>
        {/* ...tvoje polia tu ostávajú bezo zmeny... */}
      </form>
    </div>
  );
}

export default AddEmployee;
